package com.srj.productapp.dataobject Constants {    const val BASE_URL = "https://aabae193-bed4-4987-aa3e-0214167fa3f6.mock.pstmn.io"    const val PRODUCT_LIST_ENDPOINT = "/products"    const val PROFILE_ENDPOINT = "/profile"}object Utils {    fun getEndpoint(endpoint: String): String = Constants.BASE_URL + endpoint}interface Validator {    fun isValid(input: String): Boolean}object EmailValidator : Validator {    private val regex = Regex("^[A-Za-z0-9+_.-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}$")    override fun isValid(input: String): Boolean = regex.matches(input)}